import "./login.css"
import { useState } from "react"
import React from 'react'
import { useHistory } from "react-router-dom"

const Login = () => {

    const history = useHistory()
    const [userMail, setUserMail] = useState([])
    const [userPassword, setUserPassword] = useState([])

    const handleUserMailInput = (e) => {
        setUserMail(e.target.value)
    }
    const handleUserPasswordInput = (e) => {
        setUserPassword(e.target.value)
    }
    const loginChecker = () => {
        // Perform check on user's input email & password


        // Success then route to homepage
        let path = "/home"
        history.push(path)

    }

    return (
        <div className="login">
            <div className="loginWrapper">

                <div className="loginLeft">
                    <h3 className="loginLogo">Social Appie</h3>
                    <span className="loginDesc">Please sign in or sign up.</span>

                </div>

                <div className="loginRight"></div>
                    <div className="loginBox">
                        <input id="input1 "type="email" value={userMail} onChange={handleUserMailInput} placeholder="Email address" className="loginInput"/>
                        <input id="input2" type="Password" value={userPassword} onChange={handleUserPasswordInput} placeholder="Password" className="loginInput"/>
                        <button className="loginBtn" onClick={loginChecker}>Log in</button>
                        <button className="loginRegisterBtn">Sign up?</button>
                    </div>

            </div>

        </div>
    )
}

export default Login
