import React from 'react'
import "./home.css"
import Feed from '../../components/feed/Feed'
import Rightbar from '../../components/rightbar/Rightbar'
import Sidebar from '../../components/sidebar/Sidebar'
import Topbar from '../../components/topbar/Topbar'
import { useEffect } from 'react'
import { useState } from 'react'

const Home = (props) => {

    const tempArrForInitialState = new Array(100)
    if (localStorage.getItem("friends")){
        let friendsStr = localStorage.getItem("friends")
        console.log(friendsStr.split(","))
    }
    const [friendsArr, setFriends] = useState(tempArrForInitialState)

    // Fetch users from dummy server
    useEffect(() => {
        const getFriends = async () => {
            const serverFriends = await fetchFriends()
            // console.log(serverFriends)
            setFriends(serverFriends)
        }
        getFriends()
    }, [])
    const fetchFriends = async() => {
        const res = await fetch("https://jsonplaceholder.typicode.com/users")
        const data = await res.json()
        return data
    }

    const deleteFriendFromList = (e) => {
        let temp = friendsArr.filter( (friend) => friend.id != e.currentTarget.name)
        console.log(`I'm at Home.js deleting friend ${e.currentTarget.name}!`)

        // Update friends at localStorage
        let temp2 = friendsArr.map((friend)=>(friend.id))
        temp2.filter((friend) => friend.id != e.currentTarget.name)
        localStorage.setItem("friends", temp2)

        setFriends(temp)
    }

    return (
        <>
            <Topbar />
            <div className="homeContainer">
                <Sidebar currUser={props.currUser} friends={friendsArr} deleteFriendFromList={deleteFriendFromList}/>
                <Feed currUser={props.currUser} friends={friendsArr}/>
                <Rightbar currUser={props.currUser}/>
            </div>
        </>
    )
}
export default Home
