import Login from "./pages/login/Login";
import Profile from "./pages/profile/Profile";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom"
import { useState } from 'react'


function App() {

  const [currUser, setCurrUser] = useState(0)  // Pass it to Login, Home, Profile so I know which user is logged in now

  const updateCurrUser = (userID) => {
    console.log(`I'm at App.js setting localStorage for userID ${userID}`)
    console.log(localStorage)
    localStorage.setItem("currUser", userID)
    setCurrUser(userID)
  }
  if(localStorage.getItem("currUser")){
    console.log("I'm in Home.js checking localStorage for currUser trying to retain this user's login status...")
    props.updateCurrUser(parseInt(localStorage.getItem("currUser")))
  }

  return (
    <Router>
        <Switch>
          <Route exact path="/">
            <Login currUser={currUser} updateCurrUser={updateCurrUser}/>
          </Route>
          <Route exact path="/home">
            <Home currUser={currUser} updateCurrUser={updateCurrUser}/>
          </Route>
          <Route exact path="/profile">
            <Profile currUser={currUser} updateCurrUser={updateCurrUser}/>
          </Route>
        </Switch>
    </Router>

  );
}

export default App;
