import { useState } from "react"
import React from 'react'

const Login = () => {

    const [userCredentials, setUserCredentials] = useState(["", ""])

    const handleUserInput = (e) => {
        if(e.target.id === "input1"){
            let tempInputStore = userCredentials
            tempInputStore[0] = e.target.value
            setUserCredentials(tempInputStore)
        }
        else if(e.target.id === "input2"){
            let tempInputStore = userCredentials
            tempInputStore[1] = e.target.value
            setUserCredentials(tempInputStore)
        }
    }

    const loginChecker = () => {

    }

    return (
        <div className="login">
            <div className="loginWrapper">

                <div className="loginLeft">
                    <h3 className="loginLogo">Social Appie</h3>
                    <span className="loginDesc">Please sign in or sign up.</span>

                </div>

                <div className="loginRight"></div>
                    <div className="loginBox">
                        <input id="input1 "type="email" value={userCredentials[0]} onChange={handleUserInput} placeholder="Email address" className="loginInput"/>
                        <input id="input2" type="Password" value={userCredentials[1]} onChange={handleUserInput} placeholder="Password" className="loginInput"/>
                        <button className="loginBtn" onClick={loginChecker}>Log in</button>
                        <button className="loginRegisterBtn">Sign up?</button>
                    </div>

            </div>

        </div>
    )
}

export default Login
