import React from 'react'
import { DeleteForever } from '@material-ui/icons';
import { IconButton } from '@material-ui/core';
import "./sidebar.css"
import { useEffect, useState } from 'react'


const FriendsList = () => {

    const tempArrForInitialState = new Array(100)
    const [friendsArr, setFriends] = useState(tempArrForInitialState)

    // Fetch users from dummy server
    useEffect(() => {
        const getFriends = async () => {
            const serverFriends = await fetchFriends()
            // console.log(serverFriends)
            setFriends(serverFriends)
        }
        getFriends()
    }, [])
    const fetchFriends = async() => {
        const res = await fetch("https://jsonplaceholder.typicode.com/users")
        const data = await res.json()
        return data
    }

    const deleteFriendFromList = (e) => {
        let temp = friendsArr.filter( (friend) => friend.id !== e.currentTarget.name )
        setFriends(temp)
    }

    return (
            <ul className="sidebarFriendList">    

                {friendsArr.map(element => (
                    <li className="sidebarFriend" key={element.id}>
                        <img className="sidebarFriendImg" src="/assets/profile_pic_2.jpg" alt=""/>
                        <span className="sidebarFriendName">{element.username}</span>
                        {/* <button id="1" onClick={deleteFriendFromList}>click</button> */}
                        <IconButton className="sidebarFollowListUnfollow" onClick={deleteFriendFromList} name={element.id}>
                            <DeleteForever style={{fill: "red"}}/>
                        </IconButton>
                    </li>))}     
            </ul>

        )

}

export default FriendsList
