import React from 'react'
// import Feed from '../../components/feed/Feed'
// import Rightbar from '../../components/rightbar/Rightbar'
// import Sidebar from '../../components/sidebar/Sidebar'
import Topbar from '../../components/topbar/Topbar'
import "./profile.css"
import { useState } from "react"


const Profile = () => {

    const [userInput, setUserInput] = useState(["","","","",""]);
    const [currentValue, setCurrentValue] = useState([])


    let updated_input = userInput;

    const inputEmailHandler = (e) => {    
        // This is for later input checking
        updated_input[0] = e.target.value
        // This is for updating state at the moment
        let newState = userInput;
        newState[0] = String(e.target.value)
        console.log(e.target.value)
        setUserInput(newState)
    }

    const inputNumberHandler = (e) => {
        updated_input[1] = e.target.value
        let newState = userInput;
        newState[1] = e.target.value
        console.log(e.target.value)
        setUserInput(newState)
    }

    const inputZipHandler = (e) => {
        updated_input[2] = e.target.value
        let newState = userInput;
        newState[2] = e.target.value
        setUserInput(newState)
    }

    const inputPasswordHandler = (e) => {
        updated_input[3] = e.target.value   
        let newState = userInput;
        newState[3] = e.target.value
        setUserInput(newState)
    }

    const inputPasswordConfirmHandler = (e) => {
        updated_input[4] = e.target.value
        let newState = userInput;
        newState[4] = e.target.value
        setUserInput(newState)
    }

    const inputChecker = (e) => {
        // Validate the input and update the fields if success
        let errorString = "";
        let fieldsChangedStr = "";
        let password_checked = false;
        if(updated_input[0]){
            let reg_email = /^[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[A-Za-z]{2,}$/;
                if(updated_input[0].match(reg_email) == null){
                    errorString += "Please enter a valid email address\n";
                    updated_input[0] = "";
                }
                else{
                    fieldsChangedStr += ("Email Changed to "+ String(updated_input[0]));
                    updated_input[0] = "";
                }
        }
        if(updated_input[1]){
            let reg_phone = /^[1-9]{3}-[0-9]{3}-[0-9]{4}$/;
                if(updated_input[1].match(reg_phone) == null){
                    errorString += "Please enter a valid phone number\n";
                    updated_input[1] = "";
                }
                else{
                    fieldsChangedStr += ("Number Changed to "+ String(updated_input[1]));
                    updated_input[1] = "";
                }
        }
        if(updated_input[2]){
            let reg_zip = /^[0-9]{5}(?:-[0-9]{4})?$/;
                if(updated_input[2].match(reg_zip) == null){
                    errorString += "Please enter a valid zip code\n";
                    updated_input[2] = "";
                }
                else{
                    fieldsChangedStr += ("Zip code Changed to "+ String(updated_input[2]));
                    updated_input[2] = "";
                }
        }
        if(updated_input[3]){
            let reg_password = /^[\S]{6,}$/; 
                if(updated_input[3].match(reg_password) == null){
                    errorString += "Please enter a valid password\n";
                    updated_input[3] = "";
                }
                else{
                    password_checked = true
                    updated_input[3] = "";
                }
        }
        if(updated_input[4]){
                if(updated_input[4] !== updated_input[3]){
                    errorString += "Please make sure your password matches\n";
                    updated_input[4] = "";
                    updated_input[3] = "";
                }
                else{
                    fieldsChangedStr += ("Password Changed to "+ String(updated_input[4]));
                    updated_input[4] = "";
                }
        }

        // Pass checker -> set new state with these updated inputs
        setUserInput(updated_input)

    }


    return (
        <>
            <Topbar/>
            <div className="profileWrapper">
                <div className="profileTop">
                    <img className="profilePic "src="assets/profile_pic_1.jpg" alt="" />
                    <span className="profileName">Mario</span>

                    <div class="profile-work">
                        <p className="profile-work-text">LINKS</p>
                        <a className="profile-work-link" href="https://github.com/Mikerpen22">Github</a>
                        <a className="profile-work-link" href="https://www.linkedin.com/in/mh-shih">Linkedin</a>
                    </div>
                </div>

                <div className="profileMain">
                    <div className="inputSection">
                        <span className="inputPrompt">User Name:</span>
                        <input className="displayNameInput inputBox" type="text" placeholder="Unable to change"/>
                        <span className="displayCurrentValue">Mario</span>
                    </div>

                    <div className="inputSection">
                        <span className="inputPrompt">Email Address:</span>
                        <input className="displayEmailInput inputBox" type="email" value={userInput[0]} onChange={inputEmailHandler} placeholder="x@z.ab"/>
                        <span className="displayCurrentValue">ms100@rice.edu</span>
                    </div>

                    <div className="inputSection">
                        <span className="inputPrompt">Tel:</span>
                        <input className="displayNumberInput inputBox" type="tel" value={userInput[1]} onChange={inputNumberHandler} placeholder="123-123-1234"/>
                        <span className="displayCurrentValue">123-789-456</span>
                    </div>

                    <div className="inputSection">
                        <span className="inputPrompt">Zip code:</span>
                        <input className="displayZipInput inputBox" type="text" value={userInput[2]} onChange={inputZipHandler} placeholder="77665"/>
                        <span className="displayCurrentValue">55688</span>
                    </div>

                    <div className="inputSection">
                        <span className="inputPrompt">Password:</span>
                        <input className="displayPasswordInput inputBox"  value={userInput[3]} onChange={inputPasswordHandler} type="password"/>
                        <span className="displayCurrentValue">******</span>
                    </div>

                    <div className="inputSection">
                        <span className="inputPrompt">Password confirmation:</span>
                        <input className="displayPasswordConfirmInput inputBox" value={userInput[4]} onChange={inputPasswordConfirmHandler} type="password"/>
                        <span className="displayCurrentValue">******</span>
                    </div>

                    <input className="profileUpdateButton "type="submit" value="Update" onClick={inputChecker}/>
                </div>
            </div>


        </>
    )
}

export default Profile
