import "./login.css"
import { useState, useEffect } from "react"
import React from 'react'
import { useHistory } from "react-router-dom"


const Login = () => {
    const [UsersCredentials, setUsersCredentials] = useState([])
    // Fetch users email and password from dummy server
    useEffect(() => {
        const getUsersCredentials = async () => {
            const serverUsersCredentials = await fetchUsersCrendentials()
            setUsersCredentials(serverUsersCredentials)
            // console.log(UsersCredentials)
        }
        getUsersCredentials()
    })
    const fetchUsersCrendentials = async() => {
        const res = await fetch("https://jsonplaceholder.typicode.com/users")
        const data = await res.json()
        return data
    }


    const history = useHistory()
    const [userMail, setUserMail] = useState([])
    const [userPassword, setUserPassword] = useState([])

    const handleUserMailInput = (e) => {
        setUserMail(e.target.value)
    }
    const handleUserPasswordInput = (e) => {
        setUserPassword(e.target.value)
    }
    const loginChecker = () => {
        // Perform check on user's input email & password
        let userCredentialsEmail = []
        let userCredentialsPassword = []
        userCredentialsEmail = UsersCredentials.map( userObj => userObj.email )
        userCredentialsPassword = UsersCredentials.map( userObj => userObj.address.street )
        if (userCredentialsEmail.includes(userMail)){
            if(userCredentialsPassword.includes(userPassword)){
                // Success then route to homepage
                let path = "/home"
                history.push(path)
            }
            else{
                window.alert("wrong password!")
                setUserPassword("")
            }

        }
        else{
            window.alert("wrong username or password!")
            setUserMail("")
            setUserPassword("")
        }




    }

    const registerHandler = () => {

    }

    return (
        <div className="login">
            <div className="loginWrapper">

                <div className="loginLeft">
                    <h3 className="loginLogo">Social Appie</h3>
                    <span className="loginDesc">Please sign in or sign up.</span>

                </div>

                <div className="loginRight"></div>
                    <div className="loginBox">
                        <input id="input1 "type="email" value={userMail} onChange={handleUserMailInput} placeholder="Email address" className="loginInput"/>
                        <input id="input2" type="Password" value={userPassword} onChange={handleUserPasswordInput} placeholder="Password" className="loginInput"/>
                        <button className="loginBtn" onClick={loginChecker}>Log in</button>
                        <button className="loginRegisterBtn" onClick={registerHandler}>Sign up?</button>
                    </div>

            </div>

        </div>
    )
}

export default Login
